Vector = $database->loadObjectList();		return $rowsVector;	}	function create() {		$database = & JFactory::getDBO();		$database->setQuery($this->getCreateStatement());		$database->query();	}		function entriesCount() {		$database = & JFactory::getDBO();		$database->setQuery( "SELECT count(*) FROM #__security_images_logs");    	return $database->loadResult();		}	}?>