jax, $method )) {			$userAjax->$method();		}	}	function doCron( &$pluginManager )	{		$db =& JFactory::getDBO();		$cid	= JRequest::getVar('element_id', array(0), 'method', 'array');		$query = "SELECT id, plugin FROM #__fabrik_cron WHERE id IN (" . implode(',', $cid).")";		$db->setQuery($query);		$rows = $db->loadObjectList();		$tableModel =& JModel::getInstance( 'table', 'FabrikModel' );		$c = 0;		foreach ($rows as $row) {			//load in the plugin			$plugin =& $pluginManager->getPlugIn( $row->plugin, 'cron' );			$plugin->setId($row->id);			$params =& $plugin->getParams();							$thisTableModel = clone($tableModel);			$thisTableModel->setId( $params->get('table'));			$table =& $tableModel->getTable();			$total 						= $thisTableModel->getTotalRecords();			$nav =& $thisTableModel->getPagination( $total, 0, $total );			$data  = $thisTableModel->getData();			// $$$ hugh - added table model param, in case plugin wants to do further table processing			$c = $c + $plugin->process($data, $thisTableModel);		}		$query = "update #__fabrik_cron set lastrun = NOW() where id IN (" . implode(',', $cid).")";		$db->setQuery( $query );		$db->query();	}}?>