<?php// Check to ensure this file is within the rest of the frameworkdefined('JPATH_BASE') or die();// Import library dependenciesjimport('joomla.application.component.helper');class FabrikCacheHelper{		function tableSelect( $tableModel )	{		$query = $tableModel->_buildQuerySelect();		$query .= $tableModel->_buildQueryJoin();		$db =& JFactory::getDBO();		$query		= $db->replacePrefix( $query, '#__' );		return $query;	}	}?>