<?php/*** Plugin element to render fields* @package fabrikar* @author Rob Clayburn* @copyright (C) Rob Clayburn* @license http://www.gnu.org/copyleft/gpl.html GNU/GPL*/// Check to ensure this file is included in Joomla!defined('_JEXEC') or die();class imageRender{		/**	 * @param object element model	 * @param object element params	 * @param string row data for this element	 * @param object all row's data	 */	function renderTableData( &$model, &$params, $file, $oAllRowsData )	{		$this->render( $model, $params, $file, $oAllRowsData );	}		/**	 * @param object element model	 * @param object element params	 * @param string row data for this element	 * @param object all row's data	 */		function render( &$model, &$params, $file, $oAllRowsData = null )	{		// $$$ hugh - added this hack to let people use elementname__title as a title element		// for the image, to show in the lightbox popup.		// So we have to work out if we're being called from a table or form		$title = basename( $file );		if ($params->get( 'fu_title_element' ) == '') {			$title_name = $model->getFullName( true, true, false ) . '__title';		} else {			$title_name = str_replace( '.', '___', $params->get( 'fu_title_element' ) );		}		if (JRequest::getVar( 'view' ) == 'table') {			$tableModel =& $model->getTableModel();			if (array_key_exists( $title_name, $oAllRowsData )) {				$title = $oAllRowsData->$title_name;							}		}		else {			if (is_object( $model->_form )) {				if (is_array( $model->_form->_data )) {					if (array_key_exists( $title_name, $model->_form->_data )) {						$title = $model->_form->_data[$title_name];					}									}			}		}		if (strstr( $title, $model->_groupSplitter )) {			$bits = explode( $model->_groupSplitter, $title );			if (array_key_exists( $model->_repeatGroupCounter, $bits )) {				$title = $bits[$model->_repeatGroupCounter];			}		}		$element =& $model->getElement();		$file = $model->storage->getFileUrl( $file );//str_replace( "\\", "/", COM_FABRIK_LIVESITE  . $file );				$fullSize = $file;		if ($params->get( 'make_thumbnail' )  == '1') {			$file = $model->storage->_getThumb( $file );		}		$this->output = '';		if ($params->get( 'fileupload_crop' )) {		   if (JRequest::getVar( 'view' ) == 'form') {		    $this->output .= $this->_cropInterface( $model, $params, $file );		   } else {		     $file = $model->_getCropped( $fullSize );		     $this->output .=	"<a href='$fullSize' rel='lightbox[]' title='$title'><img class='fabrikLightBoxImage' src='$file' alt='$element->label' /></a>";		   }		} else {		  $this->output .=	"<a href='$fullSize' rel='lightbox[]' title='$title'><img class='fabrikLightBoxImage' src='$file' alt='$element->label' /></a>";		}	}		/**	 * render the cropping interface for the image (when in form)	 * @param object element $model	 * @param object element $params	 * @param string filename $file	 * @return string html interface to allow cropping	 */		function _cropInterface($model, $params, $file)	{		static $cropscripts;    if (!isset( $cropscripts )) {      $cropscripts = array();    }    $fullname = $model->getFullName();	  $document =& JFactory::getDocument();	  $imageid = $fullname . "_dragger";	  $divid = $fullname . "_dragger_container";	  $thumbname = $fullname . "_dragger_thumb";	  $fieldname = $fullname . "_cropcoords";	  $w = (int)$params->get('fileupload_crop_width', 50);	  $h = (int)$params->get('fileupload_crop_height', 50);	  $str = "<div id='$divid' style='position:relative;cursor:move;width:{$w}px;height:{$h}px;border:1px solid;overflow:hidden'>";	  	  $top = - $model->_cropCoords[1];	  $left = - $model->_cropCoords[0];	  	  $str .= "<img style='position:absolute;top:{$top}px;left:{$left}px;' id='$imageid' src='$file' alt='' />";	  	  $str .= "</div>";	  $str .= "<input type='hidden' name='$fieldname' id='$fieldname' value='0,0' />";	  $str .= "<input type='hidden' name='$thumbname' value='$file' />";	  $size =  getimagesize( str_replace( ' ', '%20', $file ) );	  $iw = $size[0];	  $ih = $size[1];	  	  $offsetleft = ($iw - $w) * -1;	  $offsettop = ($ih - $h) * -1;	  if (!array_key_exists( $fullname, $cropscripts )) {		  $script = "window.addEvent('domready', function(){		  	new Drag.Move('$imageid', {		  	onDrag:function(e){		  		var x = this.value.now.x.toInt() * -1;		  		var y =  this.value.now.y.toInt() * -1 ;		  				  		$('$fieldname').value = x +',' +y;		  	},		  	limit:{x:[$offsetleft, 0], y:[$offsettop, 0 ]}});		  })";		  $document->addScriptDeclaration($script);		  $cropscripts[$fullname] = true;	  }	  return $str;	}}?>