<!-- This is a sample email template. It will just print out all of the request data: --><table><?phpforeach ($_REQUEST as $key => $val) {  echo "<tr><td>$key</td><td>";  if (is_array($val)){  	foreach ($val as $v) {  		if (is_array($v)) {  			echo implode("<br>", $v);	  	}else{	  		echo implode("<br>", $val);	  	}  	}  } else {  	echo $val;  }  echo "</td></tr>";}?></table>