<?php//@TODO: not at all tested!/*** Run some php when the form is submitted* @package Joomla* @subpackage Fabrik* @author Rob Clayburn* @copyright (C) Rob Clayburn* @license http://www.gnu.org/copyleft/gpl.html GNU/GPL*/// Check to ensure this file is included in Joomla!defined('_JEXEC') or die();//require the abstract plugin classrequire_once( COM_FABRIK_FRONTEND.DS.'models'.DS.'plugin-form.php' );class FabrikModelFabrikPHP extends FabrikModelFormPlugin { 		var $_counter = null;		var $_data = null;	/**	* Constructor	*/	function __construct()	{		parent::__construct();	}		/**	 * get the html to insert at the bottom of the form(non-PHPdoc)	 * @see components/com_fabrik/models/FabrikModelFormPlugin#getBottomContent_result()	 */		function getBottomContent_result( $c )	{		return $this->_data;	}	/**	 * store the html to insert at the bottom of the form(non-PHPdoc)	 * @see components/com_fabrik/models/FabrikModelFormPlugin#getBottomContent()	 */	function getBottomContent( &$params, &$formModel )	{		$this->_data = '';		if( $params->get('only_process_curl') == 'getBottomContent' ){			$this->_data = $this->_runPHP( $params, $formModel ); 			if ($this->_data === false) {				return false;			} 		}		return true;	}		/**	 * get the html to insert at the top of the form(non-PHPdoc)	 * @see components/com_fabrik/models/FabrikModelFormPlugin#getTopContent_result()	 */	function getTopContent_result( $c )	{		return $this->_data;	}		/**	 * store the html to insert at the top of the form(non-PHPdoc)	 * @see components/com_fabrik/models/FabrikModelFormPlugin#getTopContent()	 */		function getTopContent( &$params, &$formModel )	{		$this->_data = '';		if( $params->get('only_process_curl') == 'getTopContent' ){			$this->_data = $this->_runPHP( $params, $formModel ); 			if ($this->_data === false) {				return false;			} 		}		return true;	}		/**	 * get the html to insert after the end of the form(non-PHPdoc)	 * @see components/com_fabrik/models/FabrikModelFormPlugin#getEndContent_result()	 */	function getEndContent_result( $c )	{		return $this->_data;	}		/**	 * store the html to insert after the end of the form(non-PHPdoc)	 * @see components/com_fabrik/models/FabrikModelFormPlugin#getEndContent()	 */		function getEndContent( &$params, &$formModel )	{		$this->_data = '';		if( $params->get('only_process_curl') == 'getEndContent' ){			$this->_data = $this->_runPHP( $params, $formModel ); 			if ($this->_data === false) {				return false;			} 		}		return true;	}	 	 	function onBeforeProcess( &$params, &$formModel ) 	{ 		if( $params->get('only_process_curl') == 'onBeforeProcess' ){ 			if ($this->_runPHP( $params, $formModel ) === false) {				return false;			} 		} 		return true; 	} 	 	function onBeforeStore( &$params, &$formModel ) 	{ 	 	if( $params->get('only_process_curl') == 'onBeforeStore' ){ 			if ($this->_runPHP( $params, $formModel ) === false) {				return false;			} 		} 		return true; 	} 	 	 	function onBeforeCalculations( &$params, &$formModel ) 	{ 	 	if( $params->get('only_process_curl') == 'onBeforeCalculations' ){ 			if ($this->_runPHP( $params, $formModel ) === false) {				return false;			} 		} 		return true; 	} 	 	function onAfterProcess( &$params, &$formModel ) 	{ 	 	if( $params->get('only_process_curl') == 'onAfterProcess' ){ 			if ($this->_runPHP( $params, $formModel ) === false) {				return false;			} 		} 		return true; 	} 	 	function onLoad( &$params, &$formModel ) 	{ 	 	if( $params->get('only_process_curl') == 'onLoad' ){ 			$this->_runPHP( $params, $formModel ); 		} 		return true; 	} 	 	/** 	 * process the plugin, called when form is submitted 	 * 	 * @param object $params 	 * @param object form 	 */ 	function onError( &$params, &$formModel ) 	{ 	 	if ($params->get('only_process_curl') == 'onError') { 			$this->_runPHP( $params, $formModel ); 		} 		return true;	}		function _runPHP( &$params, &$formModel )	{		/**		 * if you want to modify the submitted form data		 * and are calling Start of form submission (onBeforeProcess) then you will need to 		 * set the POST data with		 * JRequest::setVar('tablename___elementname', $newvalue);		 * 		 * if you want to modify the submitted form data		 * and are calling After any images have been uploaded (onBeforeStore)		 * set the form models data with 		 * $this->_data['tablename___elementname'] = $newvalue;		 */				$this->_data =& $formModel->_formData;		if ($params->get('form_php_file') == -1) {			return eval( $params->get('curl_code', ''));		} else {						// $$$ hugh - give them some way of getting at form data			// (I'm never sure if $_REQUEST is 'safe', i.e. if it has post-validation data)			global $fabrikFormData;			// for some reason, =& wasn't working??			$fabrikFormData = $this->_data;			$php_file = COM_FABRIK_FRONTEND.DS.'plugins'.DS.'form'.DS.'fabrikphp'.DS.'scripts'.DS.$params->get('form_php_file');						$method = $params->get('only_process_curl');			if ($method == 'getBottomContent' || $method == 'getTopContent' ){				//for these types of scripts any out put you want to inject into the form should be echo'd out 				ob_start();				require( $php_file );				$output = ob_get_contents();				ob_end_clean();				return $output;			} else {				$php_result = require($php_file);			}			if ($php_result === false) {				return false;			}		}		return true;	}}?>