<?php/*** @version		1.1.0* @package		AceSEF* @subpackage	AceSEF* @copyright	2009 JoomAce LLC, www.joomace.net* @license		GNU/GPL*/// No Permissiondefined('_JEXEC') or die('Restricted Access');// Importsjimport('joomla.application.component.model');jimport('joomla.filesystem.file');jimport('joomla.filesystem.folder');jimport('joomla.installer.helper');// Upgrade Model Classclass AcesefModelUpgrade extends JModel {		// Main constructer	function __construct() {		parent::__construct();	}	// Upgrade    function upgrade() {		$package = $this->_getPackageFromUpload();        // Was the package unpacked?        if (!$package) {            $this->setState('message', 'Unable to find install package.');            return false;        }        // Get current version        $curVersion = $this->getAceSEFVersion();        if (empty($curVersion)) {            $this->setState('message', JText::_('Could not find current version.'));            JFolder::delete($package['dir']);            return false;        }        // Create an array of upgrade files        $upgradeDir = $package['dir'].DS.'upgrade';        $upgradeFiles = JFolder::files($upgradeDir, '.php$');        if (empty($upgradeFiles)) {            $this->setState('message', JText::_('This package does not contain any upgrade informations.'));            JFolder::delete($package['dir']);            return false;        }        // check if current version is upgradeable with downloaded package        $reinstall = false;        if (!in_array($curVersion . '.php', $upgradeFiles)) {            if (($fromServer != 1) && empty($extension)) {                // check if current version is being manually reinstalled with the same version package                $xmlFile = $package['dir'].DS.'acesef.xml';                $packVersion = $this->_getXmlText($xmlFile, 'version');                if (($packVersion == $curVersion) && JFile::exists($upgradeDir.DS.'reinstall.php')) {                    // initiate the reinstall                    $reinstall = true;                    global $mainframe;                    $mainframe->enqueueMessage(JText::_(ACESEF_UPGRADE_REINSTALL));                }            }            if (!$reinstall) {                $this->setState('message', JText::_(ACESEF_UPGRADE_UNSUCCESS));                JFolder::delete($package['dir']);                return false;            }        }        natcasesort($upgradeFiles);        // prepare arrays of upgrade operations and functions to manipulate them        $this->_fileError = false;        $this->_fileList = array();        $this->_sqlList = array();        $this->_scriptList = array();        if (!$reinstall) {            // load each upgrade file starting with current version in ascending order            foreach ($upgradeFiles as $uFile) {                if (!eregi("^[0-9]+\.[0-9]+\.[0-9]+\.php$", $uFile)) {                    continue;                }                if (strnatcasecmp($uFile, $curVersion.".php") >= 0) {                    require_once($upgradeDir.DS.$uFile);                }            }        } else {            // create list of all files to upgrade            require_once($upgradeDir.DS.'reinstall.php');        }        if ($this->_fileError == false) {            // set errors variable            $errors = false;            // first of all check if all the files are writeable            foreach ($this->_fileList as $dest => $op) {                $file = JPath::clean(JPATH_ROOT.DS.$dest);                // check if source file is present in upgrade package                if ($op->operation == 'upgrade') {                    $from = JPath::clean($package['dir'].DS.$op->packagePath);                    if( !JFile::exists($from) ) {                        JError::raiseWarning( 100, JText::_('File does not exist in upgrade package').': '.$op->packagePath );                        $errors = true;                    }                }                if ((($op->operation == 'delete') && (JFile::exists($file))) || (($op->operation == 'upgrade') && (!JFile::exists($file)))) {                    // if the file is to be deleted or created, the file's directory must be writable                    $dir = dirname($file);                    if (!JFolder::exists($dir)) {                        // we need to create the directory where the file is to be created                        if(!JFolder::create($dir)) {                            JError::raiseWarning( 100, JText::_('Directory could not be created') . ': ' . $dir );                            $errors = true;                        }                    }                    if (!is_writable($dir)) {                        if (!JPath::setPermissions($dir, '0755', '0777')) {                            JError::raiseWarning( 100, JText::_('Directory not writeable') . ': ' . $dir );                            $errors = true;                        }                    }                }                elseif ($op->operation == 'upgrade') {                    // the file itself must be writeable                    if (!is_writable($file)) {                        if (!JPath::setPermissions($file, '0755', '0777')) {                            JError::raiseWarning( 100, JText::_('File not writeable') . ': ' . $file );                            $errors = true;                        }                    }                }            }            if (!$errors) {                $db =& JFactory::getDBO();                // execute SQL queries                foreach ($this->_sqlList as $sql) {                    $db->setQuery($sql);                    if( !$db->query() ) {                        JError::raiseWarning(100, JText::_('Unable to execute SQL query') . ': ' . $sql);                        $errors = true;                    }                }                // perform file operations                foreach ($this->_fileList as $dest => $op) {                    if ($op->operation == 'delete') {                        $file = JPath::clean(JPATH_ROOT.DS.$dest);                        if (JFile::exists($file)) {                            $success = JFile::delete($file);                            if (!$success) {                                JError::raiseWarning(100, JText::_('Could not delete file. Please, check the write permissions on').' '.$dest);                                $errors = true;                            }                        }                    }                    elseif ($op->operation == 'upgrade') {                        $from = JPath::clean($package['dir'].DS.$op->packagePath);                        $to = JPath::clean(JPATH_ROOT.DS.$dest);                        $destDir = dirname($to);                        // create the destination directory if needed                        if (!JFolder::exists($destDir)) {                            JFolder::create($destDir);                        }                        $success = JFile::copy($from, $to);                        if (!$success) {                            JError::raiseWarning(100, JText::_('Could not rewrite file. Please, check the write permissions on').' '.$dest);                            $errors = true;                        }                    }                }                // run scripts                foreach ($this->_scriptList as $script) {                    $file = JPath::clean($package['dir'].DS.$script);                    if( !JFile::exists($file) ) {                        JError::raiseWarning(100, JText::_('Could not find script file').': '.$script);                        $errors = true;                    } else {                        include($file);                    }                }            }            if (!$errors) {                $this->setState('message', JText::_('AceSEF') . ' ' . JText::_('successfully upgraded.'));            }            else {                $this->setState('message', JText::_('Errors detected when upgrading AceSEF. Please check the errors reported above and repeat the upgrade process.'));            }        }        JFolder::delete($package['dir']);        return true;    }					// Adds a file operation to $fileList    // $joomlaPath - destination file path (e.g. '/administrator/components/com_acesef/admin.acesef.php')    // $operation - can be 'delete' or 'upgrade'    // $packagePath - source file path in upgrade package if $operation is 'upgrade' (e.g. '/admin.acesef.php')	function _addFileOp($joomlaPath, $operation, $packagePath = '') {        if (!in_array($operation, array('upgrade', 'delete'))) {            $this->fileError = true;            JError::raiseWarning(100, JText::_('Invalid upgrade operation') . ': ' . $operation);            return false;        }        // Do not check if file in package exists - it may be deleted in some future version during upgrade        // It will be checked before running file operations        $file = new stdClass();        $file->operation = $operation;        $file->packagePath = $packagePath;        $this->_fileList[$joomlaPath] = $file;    }		function _addSQL($sql) {        $this->_sqlList[] = $sql;    }    function _addScript($script) {        $this->_scriptList[] = $script;    }		function _getPackageFromUpload() {        // Get the uploaded file information        $userfile = JRequest::getVar('install_package', null, 'files', 'array' );        // Make sure that file uploads are enabled in php        if (!(bool) ini_get('file_uploads')) {            JError::raiseWarning(100, JText::_('WARNINSTALLFILE'));            return false;        }        // Make sure that zlib is loaded so that the package can be unpacked        if (!extension_loaded('zlib')) {            JError::raiseWarning(100, JText::_('WARNINSTALLZLIB'));            return false;        }        // If there is no uploaded file, we have a problem...        if (!is_array($userfile) ) {            JError::raiseWarning(100, JText::_('No file selected'));            return false;        }        // Check if there was a problem uploading the file.        if ( $userfile['error'] || $userfile['size'] < 1 )        {            JError::raiseWarning(100, JText::_('WARNINSTALLUPLOADERROR'));            return false;        }        // Build the appropriate paths        $config =& JFactory::getConfig();        $tmp_dest = $config->getValue('config.tmp_path').DS.$userfile['name'];        $tmp_src  = $userfile['tmp_name'];        // Move uploaded file        jimport('joomla.filesystem.file');        $uploaded = JFile::upload($tmp_src, $tmp_dest);        // Unpack the downloaded package file        $package = JInstallerHelper::unpack($tmp_dest);        // Delete the package file        JFile::delete($tmp_dest);        return $package;    }		// Version Text	function _getXmlText($file, $variable) {        // Try to find variable        $value = null;        if (JFile::exists($file)) {            $xml =& JFactory::getXMLParser('Simple');            if ($xml->loadFile($file)) {                $root =& $xml->document;                $element =& $root->getElementByPath($variable);                $value = $element ? $element->data() : '';            }        }        return $value;    }		function getAceSEFVersion() {        static $version;        if (!isset($version)) {            $xml = JFactory::getXMLParser('Simple');            $xmlFile = JPATH_ADMINISTRATOR.DS.'components'.DS.'com_acesef'.DS.'acesef.xml';            if (JFile::exists($xmlFile)) {                if ($xml->loadFile($xmlFile)) {                    $root =& $xml->document;                    $element =& $root->getElementByPath('version');                    $version = $element ? $element->data() : '';                }            }        }        return $version;    }}?>